mapper.select:
SELECT T1.*,T2.* FROM ( SELECT B.BOARD_ID as boardId, B.TITLE, B.READ_CNT as readCnt, B.CONTENTS, NVL(B.FILE_ID,'0') as fileId, B.REG_ID as regId, CASE TO_CHAR(B.reg_dt, 'yyyy/mm/dd') when TO_CHAR(sysdate, 'yyyy/mm/dd') THEN TO_CHAR(sysdate, 'HH24:MI') ELSE TO_CHAR(B.reg_dt, 'yyyy/mm/dd') END AS regDt, B.rnum as num FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT * FROM BOARD_ATTR WHERE 1=1 AND REG_DT > to_date('19700101','yyyymmdd')
ORDER BY reg_dt DESC )A WHERE rownum <= (#{pageSize} * ( #{pageNum}-1)+ #{pageSize} ) ) B WHERE B.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM BOARD_ATTR WHERE 1=1
)T2

mapper.update:
UPDATE BOARD_ATTR
SET READ_CNT = (SELECT NVL(MAX(READ_CNT),0)+1 FROM BOARD_ATTR WHERE  BOARD_ID = #{boardId,jdbcType=VARCHAR})
WHERE  BOARD_ID = #{boardId, jdbcType=VARCHAR}

 UPDATE 	BOARD_ATTR
	SET   TITLE 	=  #{title, jdbcType=VARCHAR}
   		 ,CONTENTS 	=  #{contents, jdbcType=VARCHAR}
   		 ,FILE_ID	=  #{fileId,jdbcType=VARCHAR}
    	 ,REG_ID 	=  #{regId, jdbcType=VARCHAR}
         ,REG_DT	= SYSDATE
WHERE  BOARD_ID 	=  #{boardId,jdbcType=VARCHAR}
